package systemcontract

import (
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/contracts/system"
	"github.com/ethereum/go-ethereum/core"
	"github.com/ethereum/go-ethereum/core/state"
	"github.com/ethereum/go-ethereum/core/types"
	"github.com/ethereum/go-ethereum/log"
	"github.com/ethereum/go-ethereum/params"
)

var (
	punishV2Code = ""
)

func WaterdropHardFork() []IUpgradeAction {
	return []IUpgradeAction{
		&PunishV2HardFork{},
	}
}

type PunishV2HardFork struct {
}

func (s *PunishV2HardFork) GetName() string {
	return system.PunishContractName
}

func (s *PunishV2HardFork) DoUpdate(state *state.StateDB, header *types.Header, chainContext core.ChainContext, config *params.ChainConfig) (err error) {
	contractCode := common.FromHex(punishV2Code)

	//write code to sys contract
	state.SetCode(system.PunishV1Contract, contractCode)
	log.Debug("Write code to system contract account", "addr", system.PunishV1Contract.String(), "code", punishV2Code)
	return
}
